import groovy.sql.Sql

def holder = new com.eviware.soapui.support.XmlHolder(mockRequest.requestContent)
def verMsgId = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:acmt.023.001.03' //document:MsgId")
def payMsgId = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' //document:MsgId")
def retPayMsgId = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.004.001.11' //document:MsgId")
def reqPayMsgId = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.028.001.05' //document:MsgId")
def context = context
if (verMsgId != null && !verMsgId.isBlank()) {
    println("verfication")
    def verification = new AccountVerification()
    verification.verify(holder, context)
} else if (payMsgId != null && !payMsgId.isBlank()) {
    println("payment")
    def payment = new PushPayment()
    payment.executePayment(holder, context, requestContext)
} else if (retPayMsgId != null && !retPayMsgId.isBlank()) {
    println("return payment")
    def paymentRet = new PaymentReturn()
    paymentRet.returnPayment(holder, context,  requestContext)

} else if (reqPayMsgId != null && !reqPayMsgId.isBlank()) {
    def statusReq = new PaymentStatusRequest()
    statusReq.checkStatus(holder, context)
}


