class AccountVerification {

    public String verify(com.eviware.soapui.support.XmlHolder holder, context) {

        System.out.println("here i am");
        def MsgId = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:acmt.023.001.03' //document:MsgId")
        def Acct = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:acmt.023.001.03' (//document:Id)[5]")
        if (Acct.matches('1234567890') || Acct.matches('6784232155') || Acct.matches('1234567890') || Acct.matches('6677245016')) {
               println(" the account is rejected found")
            context.ResultResponse = "PAY_Rejected"

        } else if (Acct.matches('1001237567')) {
            context.ResultResponse = "VER_BlockedUser"
        } else if (Acct.matches('11234567890')) {
            context.ResultResponse = "VER_UserAccountNotFound"
        } else if (Acct.matches('131234567890')) {
            sleep(90000)
            context.ResultResponse = "VER_TimeOut"
        } else {
            //sleep(30000)
            context.ResultResponse = "VER_UserAccountNotFound"

        }

    }
}
