class PushPayment {

    public String executePayment(com.eviware.soapui.support.XmlHolder holder, context, requestContext) {
        def MsgId = holder.getNodeValue("declare namespace header='urn:iso:std:iso:20022:tech:xsd:head.001.001.03' //header:BizMsgIdr")
        def Acct = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' (//document:Id)[8]")
        def BizMsgIdr = holder.getNodeValue("declare namespace header='urn:iso:std:iso:20022:tech:xsd:head.001.001.03' //header:BizMsgIdr")
        def OrgnlTxId = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' //document:TxId")
        def src_fincial_institution = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' (//document:Id)[1]")
        def src_issr = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' (//document:Issr)[2]")
        def src_account = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' (//document:Id)[4]")
        def dst_financial_institution = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' (//document:Id)[2]")
        def dst_account = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' (//document:Id)[8]")
        def PmtTpInf_LclInstrm = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' //document:LclInstrm")
        def SttlmInf_SttlmMtd = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' //document:SttlmMtd")
        def lmInf_ClrSys = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' //document:ClrSys")
        def IntrBkSttlmAmt = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' //document:IntrBkSttlmAmt")
        def InstdAmt = holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.008.001.10' //document:InstdAmt")
        def TxSts = "SUCC"



        System.out.println("here i am at payment push" + context.today);

        def tr = new TransactionInfo(OrgnlTxId, MsgId, BizMsgIdr, OrgnlTxId,
                src_fincial_institution, src_issr, src_account,
                dst_financial_institution, dst_account, PmtTpInf_LclInstrm, SttlmInf_SttlmMtd,
                lmInf_ClrSys, IntrBkSttlmAmt, InstdAmt, TxSts)
        def mapper = new TransactionMapper()


        if (Acct.matches('1234567890') || Acct.matches('6784232155') || Acct.matches('1234567890') || Acct.matches('6677245016')) {
            tr.setTxSts("SUCC")
            mapper.insert(tr)
println("payment push and it is successful")
            mapper.loadTransaction(tr.getOrgnlTxId())
            context.ResultResponse = "PAY_SUCCESS"


        } else if (Acct.matches('1001237567')) {
            tr.setTxSts("ACCOUNT_BLOCKED")
            mapper.insert(tr)

            mapper.loadTransaction(tr.getOrgnlTxId())
            context.ResultResponse = "PAY_ACCOUNT_BLOCKED"
        } else if (Acct.matches('11234567890')) {
            context.ResultResponse = "PAY_UserAccountNotFound"
        } else if (Acct.matches('131234567890')) {
            sleep(90000)
            context.ResultResponse = "PAY_TimeOut"
        } else {
            //sleep(30000)
            context.ResultResponse = "PAY_serAccountNotFound"

        }

    }
}
