class PaymentReturn {
    public String returnPayment(com.eviware.soapui.support.XmlHolder holder, context,requestContext) {
        def MsgId = holder.getNodeValue("declare namespace header='urn:iso:std:iso:20022:tech:xsd:head.001.001.03' //header:BizMsgIdr")
        System.out.println("here i am     2" + MsgId);
        def OrgnlTxId =holder.getNodeValue("declare namespace document='urn:iso:std:iso:20022:tech:xsd:pacs.004.001.11' //document:OrgnlTxId")
         System.out.println("here i am     2" + OrgnlTxId);
        def mapper = new TransactionMapper()
        def tr=mapper.loadTransaction(OrgnlTxId)
        requestContext.MsgId = tr.MsgDefIdr
        requestContext.BizMsgIdr = tr.BizMsgIdr
        requestContext.OrgnlTxId = tr.OrgnlTxId
        requestContext.src_fincial_institution = tr.src_fincial_institution
        requestContext.src_issr = tr.src_issr
        requestContext.src_account = tr.src_account
        requestContext.dst_financial_institution = tr.dst_financial_institution
        requestContext.dst_account = tr.dst_account
        requestContext.PmtTpInf_LclInstrm = tr.PmtTpInf_LclInstrm
        requestContext.SttlmInf_SttlmMtd = tr.SttlmInf_SttlmMtd
        requestContext.lmInf_ClrSys = tr.lmInf_ClrSys
        requestContext.IntrBkSttlmAmt = tr.IntrBkSttlmAmt
        requestContext.InstdAmt = tr.InstdAmt
        requestContext.TxSts = tr.TxSts


        System.out.println("here i am");

        context.ResultResponse = "RET_SUCCESS"
    }

}
