class TransactionInfo {

    String id
    String transaction_id
    String MsgDefIdr
    String BizMsgIdr
    String OrgnlTxId
    String src_fincial_institution
    String src_issr
    String src_account
    String dst_financial_institution
    String dst_account
    String PmtTpInf_LclInstrm
    String SttlmInf_SttlmMtd
    String lmInf_ClrSys
    String IntrBkSttlmAmt
    String InstdAmt
    String TxSts

    TransactionInfo(transaction_id, MsgDefIdr, BizMsgIdr, OrgnlTxId,
                    src_fincial_institution, src_issr, src_account,
                    dst_financial_institution, dst_account,
                    PmtTpInf_LclInstrm, SttlmInf_SttlmMtd,
                    lmInf_ClrSys, IntrBkSttlmAmt, InstdAmt, TxSts) {
        this.transaction_id = transaction_id
        this.MsgDefIdr = MsgDefIdr
        this.BizMsgIdr = BizMsgIdr
        this.OrgnlTxId = OrgnlTxId
        this.src_fincial_institution = src_fincial_institution
        this.src_issr = src_issr
        this.src_account = src_account
        this.dst_financial_institution = dst_financial_institution
        this.dst_account = dst_account
        this.PmtTpInf_LclInstrm = PmtTpInf_LclInstrm
        this.SttlmInf_SttlmMtd = SttlmInf_SttlmMtd
        this.lmInf_ClrSys = lmInf_ClrSys
        this.IntrBkSttlmAmt = IntrBkSttlmAmt
        this.InstdAmt = InstdAmt
        this.TxSts = TxSts
    }
}

