class DbConnection {
    def host = "localhost"
    def port = "3306"
    def database = "ips"
    def userName = "ips"
    def password = "ILtektesf70"
    def db

    public connect() {
        db = Sql.newInstance("jdbc:mysql://" + host + "/" + database,
                userName,
                password)
        return db

    }
}
////////////////////////////////////////////////////////////////////////////
class TransactionMapper {
    def db

    TransactionMapper() {
        DbConnection dbConnection = new DbConnection()
        this.db = dbConnection.connect()
    }

    public insert(tr) {
        db.executeInsert("INSERT INTO  ips.ips_transaction_info " +
                " (`transaction_id`,`MsgDefIdr`,`BizMsgIdr`,`OrgnlTxId`,\n" +
                "`src_fincial_institution`,`src_issr`,`src_account`,`dst_financial_institution`,\n" +
                "`dst_account`,`PmtTpInf_LclInstrm`,`SttlmInf_SttlmMtd`,`lmInf_ClrSys`,\n" +
                "`IntrBkSttlmAmt`,`InstdAmt`,`TxSts`)" +
                "VALUES (?, ?, ?,?, ?, ?,?, ?, ?,?, ?, ?,?, ?, ?)",
                [tr.transaction_id, tr.msgDefIdr, tr.getBizMsgIdr(), tr.getOrgnlTxId(), tr.src_fincial_institution, tr.src_issr,
                 tr.src_account, tr.dst_financial_institution, tr.dst_account, tr.PmtTpInf_LclInstrm, tr.getIntrBkSttlmAmt(), tr.lmInf_ClrSys,
                 tr.IntrBkSttlmAmt, tr.InstdAmt, tr.TxSts])
    }

    public loadTransaction(trid) {
        def tr = db.firstRow('SELECT transaction_id,MsgDefIdr,BizMsgIdr,OrgnlTxId,\n' +
                'src_fincial_institution,src_issr,src_account,\n' +
                'dst_financial_institution,dst_account,\n' +
                'PmtTpInf_LclInstrm,SttlmInf_SttlmMtd,\n' +
                'lmInf_ClrSys,IntrBkSttlmAmt,InstdAmt,TxSts FROM ips.ips_transaction_info' +
                ' where transaction_id ="' + trid + '"')
        println(tr.transaction_id)
//            println(transactions.get(0).get)
        return tr;

    }
}

